String webpage = "<!DOCTYPE html> <html> <head> <title>FDRS Gateway</title> </head> <body style='background-color:#eee'> <span style='color:#036'> <h1>FDRS Gateway</h1> <form> <div> <br> <fieldset> <legend>Time Set:</legend> <br>PC Time: <p id='pctime'></p> <br> <br>Controller Time: <p id='ctrlrtime'></p> <br> <input type='button' id='butsettime' name='butsettime' value='Set Time' onclick='btnTimePressed()'> </fieldset> <br><br> <fieldset> <legend>Debug Messages</legend> <textarea name='message' style='width:600px;height:100px'>Future DBG messages</textarea> </fieldset> </div> </form><script>var Socket;var espTime=new Date();function init(){Socket=new WebSocket(\"ws://\"+window.location.hostname+\":81/\");Socket.onmessage=function(a){processCommand(a)}}function processCommand(b){var c=JSON.parse(b.data);var a=c.type;if(a.localeCompare(\"cmd_time\")==0){espTime=parseInt(c.value);document.getElementById(\"ctrlrtime\").innerHTML=espTime}else{console.log(a)}}setInterval(pcDate,1000);function pcDate(){document.getElementById(\"pctime\").innerHTML=Date()}function btnTimePressed(){}window.onload=function(a){init()};</script> </body> </html> </body> </html>";